mapper.select:
SELECT 

FROM wash_coupon a

WHERE a.id = #{id}

SELECT 

FROM wash_coupon a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.flag = #{flag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wash_coupon a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE wash_coupon SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
